public class Arithmetic0peratorExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte v1 = 10;
		byte v2 = 4;
		
		int result4 = v1 % v2;
		System.out.println("% 연산 결과 : " + result4);
		
		int a = 10;
		if(a % 2 == 0) {
			System.out.println("2의 배수입니다.");
		}
	}

}
